  static Future<RegistrationResponse> requestPin(
      {required String msisdn}) async {
    try {
      final response = await Api.postRequest(
          await InformationRetriever.getRegistrationUrl() ?? "", {
        Api.msisdnKey: msisdn,
        Api.clientKey: await InformationRetriever.getInfo2(),
        Api.scopeKey: await InformationRetriever.getInfo3(),
      }, {
        Headers.contentTypeHeader: Headers.formUrlEncodedContentType
      });
      return RegistrationResponse.fromJson(jsonDecode(response));
    } catch (exception) {
      if (exception is DioException) {
        if (exception.type == DioExceptionType.connectionError ||
            exception.type == DioExceptionType.connectionTimeout) {
          throw SocketException(exception.message ?? "");
        } else if (exception.type == DioExceptionType.badResponse) {
          throw BadRequestException();
        } else {
          throw Exception();
        }
      } else {
        throw Exception();
      }
    }
  }

  static Future<bool> checkPin(
      {required String msisdn, required String pin}) async {
    try {
      final response = await Api.postRequest(
          await InformationRetriever.getTokenUrl() ?? "",
           {
        Api.secretKey: await InformationRetriever.getInfo1(),
        Api.clientKey: await InformationRetriever.getInfo2(),
        Api.grantKey: await InformationRetriever.getInfo4(),
        Api.scopeKey: await InformationRetriever.getInfo5(),
        Api.numberKey: msisdn,
        Api.otpKey: pin
      }, {
        Headers.contentTypeHeader: Headers.formUrlEncodedContentType
      });

      //Save info
      if (await _saveCredentials(
          msisdn, TokenResponse.fromJson(jsonDecode(response)))) {
        return true;
      } else {
        throw Exception();
      }
    } catch (exception) {
      if (exception is DioException) {
        if (exception.type == DioExceptionType.connectionError ||
            exception.type == DioExceptionType.connectionTimeout) {
          throw SocketException(exception.message ?? "");
        } else if (exception.type == DioExceptionType.badResponse) {
          throw BadRequestException();
        } else {
          throw Exception();
        }
      } else {
        throw Exception();
      }
    }
  }

  static Future<bool> refreshToken() async {
    try {
      print("SALES// RefreshToken // Start");
      //Send request
      final response = await Api.postRequest(
          await InformationRetriever.getTokenUrl() ?? "", {
        Api.refreshKey: await Preferences.getRefreshToken(),
        Api.secretKey: await InformationRetriever.getInfo1(),
        Api.clientKey: await InformationRetriever.getInfo2(),
        Api.scopeKey: await InformationRetriever.getInfo5(),
        Api.grantKey: await InformationRetriever.getInfo6(),
      }, {
        Headers.contentTypeHeader: Headers.formUrlEncodedContentType,
        Headers.acceptHeader: "*/*"
      });

      //Save info
      if (await _saveTokens(TokenResponse.fromJson(jsonDecode(response)))) {
        print("SALES// RefreshToken // Ok");
        return true;
      }
    } catch (exception) {
      //Error processing RefreshToken
    }
    print("SALES// RefreshToken // NOk");
    return false;
  }

  static Future<ApimResponse?> getInfo(
      BaseController? presenter, List<String> includes) async {
    try {
      ///Check number
      print("SALES// getInfo // Start");
      var msisdn = await Preferences.getMobileNumber();
      if (msisdn == null || msisdn.isEmpty) {
        presenter != null
            ? presenter.updateStatus(Pair(Status.error, Reason.notAllowed))
            : throw BadRequestException();
      }

      final response = await Api.getRequest(
          "${await InformationRetriever.getInfoUrl()}/$msisdn",
          true,
          true,
          {Api.includeKey: includes.join(",")});

      return ApimResponse.fromJson(jsonDecode(response));
    } catch (e) {
      if (e is BadRequestException || e is UnauthorizedException) {
        presenter != null
            ? presenter.updateStatus(Pair(Status.error, Reason.notFound))
            : throw BadRequestException();
      } else if (e is DioException) {
        if (e.error is SocketException || e.error is TimeoutException) {
          presenter != null
              ? presenter.updateStatus(Pair(Status.error, Reason.network))
              : throw SocketException(e.message ?? "");
        } else {
          presenter != null
              ? presenter.updateStatus(Pair(Status.error, Reason.unknown))
              : throw Exception();
        }
      } else {
        presenter != null
            ? presenter.updateStatus(Pair(Status.error, Reason.unknown))
            : throw Exception();
      }
      return null;
    }
  }

  static Future<ApimResponse?> activate(
      BaseController? presenter, String? code) async {
    try {
      ///Check number
      var msisdn = await Preferences.getMobileNumber();
      if (msisdn == null) {
        presenter != null
            ? presenter.updateStatus(Pair(Status.error, Reason.notAllowed))
            : throw BadRequestException();
      }

      var body = {
        "data": {"id": "$code", "type": "products"}
      };
print("||");
print("||");
print("codeis========================================================>$code");
print("||");
print("||");
print("||");




      final response = await Api.postRequestAuthenticated(
          "${await InformationRetriever.getInfoUrl()}/$msisdn/subscription-product",
          true,
          true,
          body);

      return ApimResponse.fromJson(jsonDecode(response));
    } catch (e) {
      if (e is UnauthorizedException) {
        presenter != null
            ? presenter
                .updateStatus(e.status ?? Pair(Status.error, Reason.unknown))
            : throw UnauthorizedException(status: e.status);
      } else if (e is DioException) {
        if (e.error is SocketException || e.error is TimeoutException) {
          presenter != null
              ? presenter.updateStatus(Pair(Status.error, Reason.network))
              : throw SocketException(e.message ?? "");
        } else {
          presenter != null
              ? presenter.updateStatus(Pair(Status.error, Reason.unknown))
              : throw Exception();
        }
      } else {
        presenter != null
            ? presenter.updateStatus(Pair(Status.error, Reason.unknown))
            : throw Exception();
      }
      return null;
    }
  }

  static Future<bool> _saveCredentials(
      String msisdn, TokenResponse tokenInfo) async {
    print(
        "API//_saveCredentials:::${tokenInfo.tokenType}/${tokenInfo.accessToken}/${tokenInfo.refreshToken}");
    return await Preferences.setMobileNumber(msisdn) &&
        await Preferences.setTokenType(tokenInfo.tokenType) &&
        await Preferences.setAccessToken(tokenInfo.accessToken) &&
        await Preferences.setRefreshToken(tokenInfo.refreshToken);
  }

  static Future<bool> _saveTokens(TokenResponse tokenInfo) async {
    print(
        "API//_saveTokens:::${tokenInfo.tokenType}/${tokenInfo.accessToken}/${tokenInfo.refreshToken}");
    return await Preferences.setTokenType(tokenInfo.tokenType) &&
        await Preferences.setAccessToken(tokenInfo.accessToken) &&
        await Preferences.setRefreshToken(tokenInfo.refreshToken);
  }

